INSERT INTO `news` (`ID`, `TITLE`, `SUMMARY`, `DETAIL`, `IMAGE`, `DATE`, `HEADLINE`) VALUES
(1, 'Dr. Yankı Yazgan''dan seminer', 'Bir anne-baba inisiyatifi olan Otizm Akademisi''nin organizasyonudur. ', '<p>\r\n	<b style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Dr. Yankı Yazgan&#39;dan seminer:</b><span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">&nbsp;Otizm ve otizme benzeyen/otizmle ilişkili durumlar. Tanı karmaşası, n&ouml;robiyoloji araştırmaları, tedavi se&ccedil;enekleri, anne babanın rol&uuml; ve mesleki işbirliği konularında bilgi ve kanaat paylaşımı, soruların tartışılması.&nbsp;</span><br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Bir anne-baba inisiyatifi olan Otizm Akademisi&#39;nin organizasyonudur.&nbsp;</span><br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<b style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Tarih:</b><span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">&nbsp;27 Haziran 2012, &Ccedil;arşamba&nbsp;</span><br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<b style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Saat:</b><span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">&nbsp;18:00 - 20:30&nbsp;</span><br style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; " />\r\n	<b style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Yer:</b><span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">&nbsp;Bilgi &Uuml;niversitesi, Dolapdere Kamp&uuml;s&uuml; / İstanbul</span></p>\r\n', 'otizm.jpg', '20121024', 0),
(2, 'Panel Otizm 2012', 'Katılımın ücretsiz olduğu panele davetlisiniz.', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 14px; text-align: justify; ">Tohum Otizm Vakfı Bilgi &Uuml;niversitesi Kuştepe B&uuml;y&uuml;k Salon&rsquo;da 11 Nisan 2012 &Ccedil;arşamba g&uuml;n&uuml; Saat 10.00&ndash;12.00 arasında &quot;Otizm 2012&quot; konulu panel ger&ccedil;ekleştirilecektir. Katılımın &uuml;cretsiz olduğu panele davetlisiniz.</span></p>\r\n<p style="text-align: justify; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; ">\r\n	<b><u>Konuşmacılar ve konular;</u></b></p>\r\n<p style="text-align: justify; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; font-size: 13px; ">\r\n	Prof. Dr. Nahit Motavalli Mukaddes &quot;Otizmde tanı ve tedavi: 2012 de neredeyiz?&quot;&nbsp;<br />\r\n	Do&ccedil;. Dr. Binyamin Birkan &quot;Otizmli Bireylerin Eğitiminde UDA Modeli&quot; Prof. Dr. H&uuml;lya Kayıhan &quot;Otizm ve Ergoterapi&quot;</p>\r\n', 'otizm1.jpg', '20121024', 0),
(3, 'Otizm Nedir?', 'Otizm, sosyal ve iletişim becerilerinin oluşmasını etkileyen bir gelişim bozukluğudur.', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 19.983333587646484px; ">Otizm, sosyal ve iletişim becerilerinin oluşmasını etkileyen bir gelişim bozukluğudur. Otizm genellikle yaşamın ilk 2 yılında ortaya &ccedil;ıkar. Otistik &ccedil;ocuklar genelde &ouml;ğrenme zorluğu &ccedil;ekerler. Otistik &ccedil;ocukların b&uuml;y&uuml;k bir kısmında farklı seviyelerde zeka geriliği g&ouml;r&uuml;lse de, zeka seviyeleri normal otistik &ccedil;ocuklar da vardır. Ancak genel zeka seviyeleri ne olursa olsun, Otistik &ccedil;ocuklar &ccedil;evrelerindeki d&uuml;nyayı algılamakta ortak bir zorluk &ccedil;ekerler</span></p>\r\n<p style="padding: 0px; margin: 0px 0px 1.53846em; font-family: Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); line-height: 19.983333587646484px; ">\r\n	Bir annenin doğum sonrası &ccedil;ocuğunun (t&uuml;m &ouml;z&uuml;r grupları dahil olmak &uuml;zere) &ouml;z&uuml;rl&uuml; olma oranı %2dir; Otistik olması oranı ise %0.5&prime;tir (eskiden bu oran 4/10.000 olarak değerlendirilirdi). Bir otistik &ccedil;ocuktan sonra, ikinci &ccedil;ocukta otizmin ortaya &ccedil;ıkması riski %3 d&uuml;r. Otizm erkek &ccedil;ocuklarda kız &ccedil;ocuklarından 4 kat daha fazla g&ouml;r&uuml;nmektedir Her &ccedil;ocuktaki otistik belirtiler ve bunların seviyesi farklılık g&ouml;sterebilir, bu nedenle otizmin seviyelerini kategorize etmek g&uuml;&ccedil;t&uuml;r. Ayrıca, Asperger Sendromu ve Rett Sendromu olarak bilinen otizm formları da bulunmaktadır.</p>\r\n', 'aut1.jpg', '20121026', 1),
(4, 'Otizmin Belirtileri Nelerdir?', 'Otistiklerde görülen belirtiler.', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 19.983333587646484px; ">Otistiklerde, etkilenme dereceleri değişse de, aşağıdaki ortak belirtiler g&ouml;r&uuml;l&uuml;r;</span></p>\r\n<ul style="padding-right: 0px; padding-left: 0px; margin: 0px 0px 1.53846em; list-style: none; color: rgb(51, 51, 51); font-family: Georgia, ''Times New Roman'', Times, serif; font-size: 13px; line-height: 19.983333587646484px; ">\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Sosyal ilişkilerde g&uuml;&ccedil;l&uuml;k Konuşma g&uuml;&ccedil;l&uuml;ğ&uuml;</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Sessiz iletişimde zorlanma</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Oyun oynama ve hayal g&uuml;c&uuml;n&uuml; kullanmada zorlanma</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Değişikliklere karşı tepki ve diren&ccedil; g&ouml;sterme</li>\r\n</ul>\r\n', 'baby2.jpg', '20121026', 1),
(5, 'Otizmin Tipik Özellikleri', 'Otistik Bir Çocuğun Özellikleri', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 19.983333587646484px; ">Otistik Bir &Ccedil;ocuk,</span></p>\r\n<ul style="padding-right: 0px; padding-left: 0px; margin: 0px 0px 1.53846em; list-style: none; color: rgb(51, 51, 51); font-family: Georgia, ''Times New Roman'', Times, serif; font-size: 13px; line-height: 19.983333587646484px; ">\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Başkalarına karşı ilgisizdir.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		G&ouml;z temasından ka&ccedil;ınır.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Başkaları ile kendiliİinden iletişim kurmaz.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		İsteklerini bir yetişkinin ellerini kullanarak belirtir.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Diğer &ccedil;ocuklarla oynamaz.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		S&uuml;rekli bir konu &uuml;zerinde konuşur. Sebepsiz şekilde ağlar, g&uuml;ler ve sebepsiz davranışlarda bulunur.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Anlamsız s&ouml;zleri &uuml;st &uuml;ste tekrarlar.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Nesneleri tutup s&uuml;rekli d&ouml;nd&uuml;rmekten hoşlanır. Değişikliklerden hoşlanmaz.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Yaratıcılık gerektiren oyunları oynayamaz.</li>\r\n	<li style="padding: 0px 0px 0px 14px; margin: 0px; background-image: url(http://www.otizm.org/wp-content/themes/neoclassical/images/bullet_text.gif); background-position: 0px 0.38462em; background-repeat: no-repeat no-repeat; ">\r\n		Bazıları yaratıcılık gerektirmeyen bazı işleri olduk&ccedil;a hızlı ve iyi yapar.</li>\r\n</ul>\r\n', 'aut2.jpg', '20121026', 1),
(6, 'Otizm ne zaman anlaşılır?', 'Otizm hastalığının bebekken anlaşılabileceği açıklandı.', '<p>\r\n	<img alt="" src="/otizm/images/news/otizm4.jpg" style="color: rgb(17, 17, 17); font-family: ''Lucida Grande'', Tahoma, Verdana, Arial; line-height: 16px; width: 230px; height: 164px; float: left; border-width: 1px; border-style: solid; margin-left: 5px; margin-right: 5px; " /></p>\r\n<p style="margin: 0px; padding: 0px 5px 10px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Lucida Grande'', Tahoma, Verdana, Arial; color: rgb(17, 17, 17); line-height: 16px; ">\r\n	Otizm hastalığının bebekken anlaşılabileceği a&ccedil;ıklandı. Uzmanlar otizm hastalığının erken yaşta tespit edilip hastalığın tedavisine b&ouml;ylece erken yaşlarda başlanacağını ve hastalığın ilerlemesinin engellenebileceğini bildirdiler.</p>\r\n<p style="margin: 0px; padding: 0px 5px 10px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Lucida Grande'', Tahoma, Verdana, Arial; color: rgb(17, 17, 17); line-height: 16px; ">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; padding: 0px 5px 10px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Lucida Grande'', Tahoma, Verdana, Arial; color: rgb(17, 17, 17); line-height: 16px; ">\r\n	Otizm hastalığının daha erken yaşlarda tespit edilebilmesi i&ccedil;in araştırma yapan bilim adamları, bu hastalığın 6 aylık bebeklerde tespit edebileceklerini s&ouml;ylediler. &nbsp;6 aylık bebeklerin beyin aktivitesini &ouml;l&ccedil;erek otizm hastalığı hakkında bilgi alınabilecek.</p>\r\n<p style="margin: 0px; padding: 0px 5px 10px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''Lucida Grande'', Tahoma, Verdana, Arial; color: rgb(17, 17, 17); line-height: 16px; ">\r\n	Araştırmayı y&uuml;r&uuml;ten bilim adamlarında Prof.Dr. Mark Johnson, g&ouml;zlerdeki farklılık ve bebeklerin tepkilerini inceleyerek otizm hastalığı konusunda bilgiler edinebiliyoruz dedi. Johnson, &rdquo; beyin aktivitesi testiyle otizm erken teşhis edilebilir bir hastalık oldu&rdquo; dedi.</p>\r\n', 'otizm2.jpg', '20121026', 0),
